import re

# Out file
outFile = 'shaders/block.properties'

# High bias blocks (stairs and slabs)
high_id_start = 15000
high_pattern = '(stairs|slab|cobblestone_wall|sandstone_wall|fence|gate|anvil|campfire|tutle_egg|lectern|torch|head|skull)'

# Low shadow bias blocks (normal blocks)
low_id_start = 10000

# Glass blocks (everything that is glass-related)
glass_id_start = 16000
glass_pattern = 'glass'

# Grass-like terrain blocks (flowers + tall grass)
grass_id_start = 9000
grass_pattern = '(flower|tulip|double_plant|tallgrass|(^minecraft:grass$)|seagrass|sugar_cane|fern)'

# All other blocks that cannot voxelize but need to be accounter for (for lighting)
door_like_id_start = 12500
door_like_pattern = '(door)'

outStr = ''
ids = ids.split(' ')

def collect_and_format(pattern: str, start_id: int, exclude: set=set()) -> list:
    collection = ''
    used_blocks = set()
    i = 0
    
    for j, block_id in enumerate(ids):
        if re.search(pattern, block_id) and j not in exclude:
            out = 'block.%i=%s' % (start_id + i, block_id)
            collection += out + '\n'
            used_blocks.add(j)
            i += 1

    return collection, used_blocks

collection = ''
used_blocks = set()
collection1, used_blocks1 = collect_and_format(high_pattern, high_id_start)
collection2, used_blocks2 = collect_and_format(glass_pattern, glass_id_start)
collection3, used_blocks3 = collect_and_format(grass_pattern, grass_id_start)
collection4, used_blocks4 = collect_and_format(door_like_pattern, door_like_id_start)

used_blocks = used_blocks1.union(used_blocks2)
used_blocks = used_blocks.union(used_blocks3)
used_blocks = used_blocks.union(used_blocks4)
collection5, _ = collect_and_format('.*', low_id_start, used_blocks)
collection = collection1 + collection2 + collection3 + collection4 + collection5

# Write to file
with open(outFile, 'w') as f:
    f.write(collection)

print('Done')